import React, { useEffect, useState } from 'react'
import './style.css';

const Todo = () => {
  const [ inputdata, setInputData] = useState("");
  const [items , setItems] =useState(get);

//   add the item function
const addItem = ()=>{
    if(!inputdata){
        alert('please fill the data');
    }
    else{
        const myNewInputData = {
            id : new Date().getTime().toString(),
            name :inputdata,
        }
        setItems([...items, myNewInputData]);
        setInputData('');
    }
}

// delete items
const deleteItem = (index) =>{
    const updatedItems= items.filter((currElem) =>{
        return currElem.id !== index;
    })
    setItems(updatedItems);
}


//remove all the data
const removeAll =() =>{
    setItems([]);
}

//add data in localstorage
useEffect(()=>{
    localStorage.setItem("mytodoList",JSON.stringify(items));
},[items]);
  return (
    <>
    <div className="main-div">
        <div className="child-div">
            <figure>
                <img src="./image/todo.svg" alt="Todo Logo" />
                <figcaption>Add Your List Here ✌</figcaption>
            </figure>
            <div className="addItems">
                <input
                  type="text"
                  placeholder="✍ Add Item"
                  className="form-control"
                  value={inputdata}
                  onChange={(e) => setInputData(e.target.value)}
                />
                <i class="fa fa-plus add-btn" onClick={addItem}></i>
            </div>
            {/* Show our items */}
            <div className="showItems">
                {
                    items.map((currElem,index) => {
                        return (
                            <div className="eachItem" key={currElem.id}>
                                <h3>{currElem.name}</h3>
                                <div className="todo-btn">
                                    <i class="far fa-edit add-btn"></i>
                                    <i class="far fa-trash-alt add-btn" onClick={() => deleteItem(currElem.id)}></i>
                                </div>
                            </div>
                        );
                    })
                }
            </div>

            {/* remove itmes */}
            <div className="showItems">
                <button className="btn effect04" data-sm-link-text='Remove All' onClick={removeAll}>
                    <span>
                    CHECK LIST
                    </span>
                </button>
            </div>
        </div>
    </div>
    </>
  )
}

export default Todo
